mapper.select:
SELECT COUNT(*) FROM V_BOARD
WHERE BOARD_STATUS = 'Y'
AND BOARD_CD = #{type}

SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT, MEMBER_ID, READ_COUNT,
			 CATEGORY_NM, BOARD_CREATE_DT, BOARD_MODIFY_DT, BOARD_CD, BOARD_NM
FROM V_BOARD
WHERE BOARD_STATUS = 'Y'
AND BOARD_NO = #{boardNo}
AND BOARD_CD = #{boardCd}

mapper.update:
UPDATE BOARD SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT * FROM ATTACHMENT
WHERE PARENT_BOARD_NO = #{boardNo}
ORDER BY FILE_LEVEL

mapper.insert:
INSERT INTO ATTACHMENT SELECT SEQ_FNO.NEXTVAL, A.* FROM (
SELECT #{item.filePath} FILE_PATH, #{item.fileName} FILE_NAME, #{item.fileLevel} FILE_LEVEL, #{item.parentBoardNo} PARENT_BOARD_NO FROM DUAL
)A

mapper.update:
UPDATE ATTACHMENT SET
FILE_PATH = #{filePath},
FILE_NAME = #{fileName}
WHERE FILE_NO = #{fileNo}
