mapper.select:
SELECT COUNT(*) FROM MEMBER 
WHERE MEMBER_ID = #{memberId}
AND MEMBER_STATUS = 'Y'

mapper.update:
UPDATE MEMBER SET
MEMBER_PHONE = #{memberPhone}, 
MEMBER_EMAIL = #{memberEmail}, 
MEMBER_ADDR = #{memberAddress}, 
MEMBER_INTEREST = #{memberInterest}
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT MEMBER_PWD
FROM MEMBER
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE MEMBER SET
MEMBER_PWD = #{newPwd}
WHERE MEMBER_NO = #{memberNo}
